std::pair<int, int> solution(std::string problem_input)
{
    unsigned long int size = problem_input.size();
    long int floor = 0;
    long int neg_pos = -1;

    for (int i = 0; i < size; i++)
    {
        if (problem_input.at(i) == '(')
            floor++;
        else if (problem_input.at(i) == ')')
            floor--;
        else
            throw std::invalid_argument("Input should only contain '(' ')'");

        if (floor < 0 && neg_pos < 0)
            neg_pos = i+1; // indexing starts at 1 in problem statement
    }

    return std::make_pair(floor, neg_pos);
}

int main()
{
    std::pair result = solution(problem_input);
    std::cout << "Part 1: " << result.first << "\n";
    std::cout << "Part 2: " << result.second << "\n";
    return 0;
}

